from .imports import*

def analyze_explanations(filename:str): #Analyze the generated explanations in a given file
  '''A function that analyzes and compares the explanations generated by train_and_explainModel.
    Parameters:
    filename:str | File name with explanations (of the form "Explainer ImportanceScores - Model Target.csv")
    
    Returns:
    Nothing
    '''
  with open(filename):
    reader = csv.reader(filename)
    rows = list(reader)
    
  importance_list = list(zip(rows[1],rows[3]))
  
  plt.plot(importance_list)
